import React from 'react';
import db from "../firebase";
import { useEffect, useState, createContext } from 'react';
import { QuerySnapshot, collection, onSnapshot } from 'firebase/firestore';

export default function Cart() {
    const [producten, setProducten] = useState([])

    useEffect(
        () => 
        onSnapshot(collection(db, "producten"), (snapshot) => 
            setProducten(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
            ),
        []
    )

    function addtocart(item) {


        producten.map((i) => {
          if (i.id == item.id) {
            i.cart = true
          }
        })
    
        db.collection('cart').doc(`${item.id}`).set(item, { merge: true })
    
      }
  return (
    <div>Cart</div>
  )
}
