import { async } from '@firebase/util';
import React, { useState } from 'react';
import { signup } from '../firebase';
import Header from './Header';
import { useRef } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Signup(){
  const navigate = useNavigate();
  const emailRef = useRef();
  const passwordRef = useRef();
  const [loading, setLoading] = useState(false);

  async function handleRegister(){
    setLoading(true);
    try {
    await signup(emailRef.current.value, passwordRef.current.value);
    navigate("/signin")
    } catch{
      alert('gebruiker bestaat al')
    }
    setLoading(false);
  }

  return(
    <div>
      <Header />
      <div>
        <input ref={emailRef} placeholder='Email'/>
        <input ref={passwordRef} type="password" placeholder='Wachtwoord'/>
      </div>
      <button disabled={loading} onClick={handleRegister} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>Register</button>

        <div>
          <label for="password" class="sr-only">Password</label>
          <input id="password" name="password" type="password" autocomplete="current-password" required class="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm" placeholder="Password"/>
        </div>
      </div>

  
    
  )
}
