import { async } from '@firebase/util';
import React, { useState } from 'react';
import { useAuth, logout} from '../firebase';
import Header from './Header';
import { useRef } from 'react';
import { useNavigate } from 'react-router-dom';

export default function signin(){

    async function handleRegister(){
        setLoading(true);
        try {
        await signup(emailRef.current.value, passwordRef.current.value);
        navigate("/signin")
        } catch{
          alert('gebruiker bestaat al')
        }
        setLoading(false);
    }

    return (
        <div>
            <Header />
            <div>
                <input ref={emailRef} placeholder='Email'/>
                <input ref={passwordRef} type="password" placeholder='Wachtwoord'/>
            </div>

            <button disabled={loading} onClick={handleRegister} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>Register</button>
        </div>
    )
}
