import React from 'react';
import Header from './Header';
import db from "../firebase";
import { useEffect, useState } from 'react';
import { collection, onSnapshot } from 'firebase/firestore';

function Home() {
     const [producten, setProducten] = useState([])
     
    useEffect(
        () => 
        onSnapshot(collection(db, "producten"), (snapshot) => 
            setProducten(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
        ),
     []
    )

  return (
    <div className='text-white justify-center'>
      <Header />  
      <ul className='text-center'>
        {producten.map((producten) =>(
            <div className='border'>
                <li key={producten.id}>
                    {producten.naam} {producten.beschrijving}
                </li>
            </div>
        ))}
      </ul>
      <div class="bg-white rounded-lg shadow-md overflow-hidden">
        <div class="relative">
            <div class="absolute top-0 right-0 p-2 bg-gray-800 text-white text-xs font-semibold rounded-bl-lg">New</div>
        </div>
        <div class="px-4 py-2">
            <h3 class="text-lg font-medium text-gray-800">Product Title</h3>
            <p class="text-gray-600 text-sm mt-1">Product description goes here</p>
            <div class="flex justify-between items-center mt-2">
            <div class="text-gray-800 font-medium">$49.99</div>
            <button class="px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded">Add to Cart</button>
            </div>
        </div>
        </div>
    </div>
  );
}

export default Home;
