import React from 'react';
import db from "../firebase";
import { useEffect, useState, createContext } from 'react';
import { QuerySnapshot, collection, onSnapshot, deleteDoc, doc } from 'firebase/firestore';

export default function Cart() {
    const [producten, setProducten] = useState([])
    const [cart, setCart] = useState([])
    const [products, setProducts] = useState([])

    useEffect(
        () => 
        onSnapshot(collection(db, "cart"), (snapshot) => 
            setCart(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
            ),
        []
    )

    useEffect(() => {
        onSnapshot(collection(db, "cart"), (querySnapshot) => {
            var p = [];
            querySnapshot.forEach((doc) => {
              p.push(doc.data());
              products.map((i) => {
                if (i.id == doc.data().id) {
                  i.cart = true
                }
              })
            });
    
            setCart(p)
          });
    
      }, []);

    function removecart(product) {

        products.map((i) => {
          if (i.id == product.id) {
            i.cart = false
          }
        })
  
        deleteDoc(doc(collection(db, 'cart'), `${product.id}`));
      }
    
  return (
    <>
    {cart.map((cart) =>( 
        <div class="border rounded p-4">
            <h2 class="text-lg font-bold">{cart.naam}</h2>
            <p class="text-gray-500">${cart.pijs}</p>
            <button class="mt-4 px-4 py-2 bg-blue-500 text-white rounded">verwijder</button>
        </div>
    ))}
    </>
  )
}
