import React from 'react';
import Header from './Header';
import db from "../firebase";
import { useEffect, useState } from 'react';
import { collection, onSnapshot, setDoc, doc} from 'firebase/firestore';
import { async } from '@firebase/util';

function Admin() {
     const [producten, setProducten] = useState([])
     
    useEffect(
        () => 
        onSnapshot(collection(db, "producten"), (snapshot) => 
            setProducten(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
        ),
     []
    )

     const handleNew = async () => {
        const docRef = doc(db, "producten", "product5");
        const payload = {name: "vaas5", beschrijving: "test5"}
        await setDoc(docRef, payload);
     }   

  return (
    <div className='text-white justify-center'>
      <Header />  
      <ul className='text-center'>
        {producten.map((producten) =>(
            <li>
                {producten.naam} {producten.beschrijving}  <button onClick={handleNew} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>wijzig</button>     
            </li>
        ))}
      </ul>
      
    </div>
  );
}

export default Admin;
