import { async } from '@firebase/util';
import React, { useState } from 'react';
import { useAuth, login} from '../firebase';
import Header from './Header';
import { useRef } from 'react';
import { useNavigate } from 'react-router-dom';


export default function Signin(){
    const navigate = useNavigate();
    const emailRef = useRef();
    const passwordRef = useRef();
    const [loading, setLoading] = useState(false);

    async function handleLogin(){
        setLoading(true);
        try {
        await login(emailRef.current.value, passwordRef.current.value);
        navigate("/account")
        } catch{
          alert('foute email of wachtwoord');
        }
        setLoading(false);
    }

    return (
        <div>
            <Header />
            <div>
                <input ref={emailRef} placeholder='Email'/>
                <input ref={passwordRef} type="password" placeholder='Wachtwoord'/>
            </div>

            <button disabled={loading} onClick={handleLogin} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>Login</button>
            <div class=" flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div class="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 class="mt-6 text-center text-3xl font-extrabold text-gray-900">
          Maak een account aan
        </h2>
      </div>

          <div class="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
            <div class=" py-8 px-4 shadow sm:rounded-lg sm:px-10">
              <form class="space-y-6" action="#" method="POST">
                <div>
                  <label for="email" class="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <div class="mt-1">
                    <input
                      type="email"
                      ref={emailRef}
                      autocomplete="email"
                      required
                      className="appearance-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                      placeholder=""
                    />
                  </div>
                </div>

                <div>
                  <label for="password" class="block text-sm font-medium text-gray-700">
                    Wachtwoord
                  </label>
                  <div class="mt-1">
                    <input
                      ref={passwordRef} 
                      type="password"
                      required
                      class="appearance-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                      placeholder=''
                    />
                  </div>
                </div>
                <div>
                  <button
                  disabled={loading} 
                  onClick={handleLoginClick}
                  type="submit"
                  className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    Register
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
        </div>
    )
}
