import React from 'react';
import Header from './Header';
import db from "../firebase";
import { useEffect, useState } from 'react';
import { collection, onSnapshot, setDoc, doc, addDoc, deleteDoc} from 'firebase/firestore';
import { async } from '@firebase/util';

function Admin() {
     const [producten, setProducten] = useState([])
     
    useEffect(
        () => 
        onSnapshot(collection(db, "producten"), (snapshot) => 
            setProducten(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
        ),
     []
    )

     const handleNew = async () => {
        const naam = prompt("geef een naam: ")
        const besch = prompt("geef een besch: ")
        const prijs = prompt("geef een prijs: ")
        const collectionRef = collection(db, "producten")
        const payload = {naam, besch, prijs}
        await addDoc(collectionRef, payload)
     }   

     const handleEdit = async (id) => {
        const naam = prompt("geef een naam: ")
        const besch = prompt("geef een besch: ")
        const prijs = prompt("geef een prijs: ")
      
        const docRef = doc(db, "producten", id);
        const payload = { naam, besch, prijs };
      
        setDoc(docRef, payload);
     }

     const handleDelete = async (id) => {
        const docRef = doc(db, "producten", id);
        await deleteDoc(docRef);
     }

  return (
    <div className='text-white justify-center'>
      <Header />  
      <ul className='text-center mt-20'>
        {producten.map((producten) =>(
            <li className=''>
                NAAM: {producten.naam} BESCHRIJVING: {producten.besch} PRIJS: â‚¬ {producten.prijs} 
                <button onClick={() => handleEdit(producten.id)} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded m-3'>wijzig</button>
                <button onClick={() => handleDelete(producten.id)} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>verwijder</button>
            </li>
        ))}
      </ul>
      <button onClick={handleNew} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>voeg toe</button>
      
    </div>
  );
}

export default Admin;
