import React from 'react';
import db from "../firebase";
import { useEffect, useState, createContext } from 'react';
import { QuerySnapshot, collection, onSnapshot } from 'firebase/firestore';

export default function Cart() {
    const [producten, setProducten] = useState([])
    const [cart, setCart] = useState([])

    useEffect(
        () => 
        onSnapshot(collection(db, "cart"), (snapshot) => 
            setCart(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
            ),
        []
    )

    
  return (
    <>
    {cart.map((cart) =>( 
        <div class="border rounded p-4">
            <h2 class="text-lg font-bold">Product 1</h2>
            <p class="text-gray-500">$19.99</p>
            <button class="mt-4 px-4 py-2 bg-blue-500 text-white rounded">Add to Cart</button>
        </div>
    ))}
    </>
  )
}
