import React from 'react'
import {Link} from 'react-router-dom';
import { useAuth} from '../firebase';

function Header() {
  const currentUser = useAuth();
  const [ loading, setLoading ] = useState(false);
  return (
    <section className="relative mx-auto">
        <nav className="flex justify-between bg-gray w-screen">
        <div className="px-5 xl:px-12 py-6 flex w-full items-center bg-gray-200 text-black">
            <a className="text-3xl font-bold font-heading">
            Webshop
            </a>
            {!currentUser && 
              <div className='flex md:flex md:flex-grow flex-row-reverse gap-4 text-lg float-right'>
                <Link to='/signup' className=''>
                    Register
                </Link>
                <Link to='/signin' className=''>
                    Log in
                </Link>
              </div>
            }
            {currentUser && 
            <button disabled={loading || !currentUser} onClick={handleLogout} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>Log uit</button>
            }
            
            <img src="https://t4.ftcdn.net/jpg/02/15/84/43/360_F_215844325_ttX9YiIIyeaR7Ne6EaLLjMAmy4GvPC69.jpg" alt="profielfoto" className='rounded-full border-3 border-solid align-middle w-10 h-10'/>
        </div>
        </nav>
    </section>
  )
}

export default Header
