import React from 'react';
import db from "../firebase";
import { useEffect, useState, createContext } from 'react';
import { QuerySnapshot, collection, onSnapshot, deleteDoc, doc, updateDoc, increment } from 'firebase/firestore';

export default function Cart() {
    const [producten, setProducten] = useState([])
    const [cart, setCart] = useState([])
    const [products, setProducts] = useState([])

    useEffect(
        () => 
        onSnapshot(collection(db, "cart"), (snapshot) => 
            setCart(snapshot.docs.map((doc) => ({...doc.data(), id: doc.id})))
            ),
        []
    )

    useEffect(() => {
        onSnapshot(collection(db, "cart"), (querySnapshot) => {
            var p = [];
            querySnapshot.forEach((doc) => {
              p.push(doc.data());
              products.map((i) => {
                if (i.id == doc.data().id) {
                  i.cart = true
                }
              })
            });
    
            setCart(p)
          });
    
      }, []);

    function removecart(product) {

        products.map((i) => {
          if (i.id == product.id) {
            i.cart = false
          }
        })
  
        deleteDoc(doc(collection(db, 'cart'), `${product.id}`));
      }

    function increase(product){
        updateDoc(doc(collection(db, 'cart'), `${product.id}`), {
            aantal: increment(1)
        });
    }

    function decrease(product){
        updateDoc(doc(collection(db, 'cart'), `${product.id}`), {
            aantal: increment(-1)
        });
    }
    
  return (
    <>
    {cart.map((cart) =>( 
        <div class="border rounded p-4">
            <h2 class="text-lg font-bold">{cart.naam}</h2>
            <p class="text-gray-500">${cart.pijs}</p>
            
            <img className='w-1/6 mx-auto mb-5 float-right' src={cart.fotoURL} alt="nog geen foto"/>
            <div class="flex justify-between">
                <button class="px-4 py-2 bg-blue-500 text-white rounded">↑</button>
                <div class="flex items-center">{cart.aantal} </div>
                <button class="px-4 py-2 bg-red-500 text-white rounded">↓</button>
                
            </div>
            <button className='btn btn-primary btn-sm' onClick={() => increase(cart)}>↑</button>
            <p class="text-gray-500">{cart.aantal}</p>
            <button className='btn btn-primary btn-sm' onClick={() => decrease(cart)}>↓</button>
            <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded" onClick={() => removecart(cart)}>delete</button>
        </div>
    ))}
    </>
  )
}
