import { useNavigate } from 'react-router-dom';

export default function Account() {
    const navigate = useNavigate();
    const currentUser = useAuth();
    const [ loading, setLoading ] = useState(false);

    async function handleLogout() {
        setLoading(false);
        try {
            await logout();
            navigate("/")
        } catch {
           alert("mislukt!") 
        }
        setLoading(true);
    }

