        <div>
            <Header/>

            <div className=''>{currentUser?.email}</div>

            <button disabled={loading || !currentUser} onClick={handleLogout} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>Log uit</button>
            
            <div>
                <input type="file" onChange={handleChange} />
                <button onClick={handleClick} className='px-3 py-1 bg-gray-800 text-white text-sm font-semibold rounded'>kies</button>
                <img src="" alt="profielfoto" className=''/>
